    const [post, setPost] = useState({})

    useEffect(() => {
        busca(`/posts/${id}`, setPost)
    }, [id])

    return (
        <main className="container flex flex--centro">
            <article className="cartao post">
                <h2 className="ccartao__titulo">{post.titulo}</h2>
                <p className="carta__texto">{post.body}</p>
            </article>
        </main>
    );
}

